import React, { Fragment, useEffect, useState } from "react";
import { Container } from "@mui/material";
import './TWoFactorEnable.css';
import { connect } from "react-redux";
import urls from "../../redux/urls";



const TwoFactorEnable = (props) => {

    console.log("qrcode props jkldjsfkj===================> qrCodegenrater", props)

    const [qrImage, setQrImage] = useState([]);
    const [loginUser, setLoginUser] = useState([]);
    const [loginId, setLoginId] = useState();
    const [activeVal, setActiveVal] = useState(1);



    console.log("loginId", loginId)



    let activateDetails = {
        id: loginId,
        twofacr: activeVal
    }

    useEffect(() => {

        if (props.auth.user) {
            setLoginUser(props.auth.user)
            setLoginId(props.auth.user.id)
        }

        if (loginId) {
            qrCodegenrater(loginId)
        }

    },[props])


    const qrCodegenrater = (loginId) => {

        fetch(`${urls.base + urls.QrCode}?id=${loginId}`)
            .then((result) => result.json()).then((qrdata) => {
                console.log("qrdata", qrdata)
                if (qrdata) {
                    setQrImage(qrdata.qrcode.image);
                }
            }).catch((error) => {
                console.log(error)
            })

    };


    console.log("qrImage fgffg", qrImage)

    // let firstremive = qrImage.slice(2);
    // let image = firstremive.lastIndexOf(' ');

    // console.log("qrImage fgffg",image)



    const acivateUserAccount = () => {
        activateTwoFactUser(activateDetails)
    }

    const activateTwoFactUser = (activateDetails) => {
        fetch(`${urls.base + urls.twoFactActive}`, {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify(activateDetails)
        }).then((res) => res.json())
            .then((result) => {
                setActiveVal(activeVal === 1 ? 0 : 1)

            })
            .catch((e) => {
                console.log(e)
            })
    }


    return (
        <Fragment>
            <section className="factor_authencate">
                <Container>
                    <div className="row">
                        <div className="qr_code">

                            <img src={qrImage} />
                        </div>
                        <button type="button" className="text-white btn btn-success font-weight-bold mr-1" onClick={acivateUserAccount} > {activeVal === 1 ? "activate" : "Deactivate"}</button>

                    </div>
                </Container>
            </section>
        </Fragment>
    )
}

const mapStateToProps = (state) => ({
    auth: state.auth,
});

export default connect(mapStateToProps)(TwoFactorEnable)
