    return(
        <Fragment>
          <section className="factor_authencate">
         <Container>
          <div className="row">
            <div className="qr_code">

                <img src={`data:image/jpeg;base64,${qrimag}`} />
            </div>
          </div>
         </Container>
          </section>
        </Fragment>
    )
}

const mapStateToProps = (state) => ({
    auth: state.auth,
  });

export default connect(mapStateToProps,{ qrCodegenrater }) (TwoFactorEnable)
