import {
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    LOGOUT_SUCCESS,
    CLEAR_AUTH_MSG,
    REGISTER_FAIL,
    REGISTER_SUCCESS,
    ACTIVATE_SUCCESS,
    ACTIVATE_FAIL,
    FORGOT_SUCCESS,
    FORGOT_FAIL,
} from "../types";

const initialState = {
    token: null,
    isAuthenticated: false,
    user: null,
    msg: null,
    credentials: null,
    twoFactorAuthentication:false
};

export default (state = initialState, action) => {
    console.log("action====>",action)
    switch (action.type) {

        case LOGIN_SUCCESS:
            return {
                ...state,
                user: action.payload.user,
                token: action.payload.token,
                twoFactorAuthenticate:action.payload,
                //credentials: action.payload.credentials,
                isAuthenticated: true,
                msg: "Logged in",
            };
        case LOGIN_FAIL:
        case REGISTER_FAIL:
        case REGISTER_SUCCESS:
        case ACTIVATE_SUCCESS:
        case ACTIVATE_FAIL:
        case FORGOT_SUCCESS:
        case FORGOT_FAIL:
        case LOGOUT_SUCCESS:
            return {
                ...state,
                token: null,
                user: null,
                isAuthenticated: false,
                msg: action.payload,
                twoFactorAuthenticate:action.payload.two_factor_authentication,
                credentials: null,
            };
        case CLEAR_AUTH_MSG:
            return {
                ...state,
                token: null,
                user: null,
                isAuthenticated: false,
                twoFactorAuthenticate:false,
                msg: null,
                credentials: null,
            };
        default:
            return state;
    }
}
