import React, { Fragment, useEffect, useState } from "react";
import { Login as LoginIcon } from "@mui/icons-material";
import { useLocation } from "react-router-dom";
import './AuthVerification.css';
import 'react-toastify/dist/ReactToastify.css';
import { connect } from "react-redux";
import urls from "../../redux/urls";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';


const AuthVerificationForm = (props) => {

    let navigate = useNavigate();
    const [verification, setVerification] = useState("");
    const location = useLocation();


    const loginId = props.auth.user.id;



    let data = {
        id: loginId,
        otp: verification
    }

    const verifiedCode = (e) => {
        // e.preventDefault()
        veriFication(data)
    }


    const veriFication = (data) => {
        console.log(data);

        fetch(`${urls.base + urls.verificationCode}`, {
            method: "POST",
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
            },
            body: JSON.stringify(data),
        })
            .then((res) => res.json())
            .then((result) => {
                window.location.href='/';

                // toast.success(result.message, {
                //     position: "top-right",
                //     className: "app_toast",
                //     autoClose: 1000,
                //     onClose: () => {

                //     },
                //   })
            }).catch((e) => {
                console.log(e)
            })

    };






    return (
        <Fragment>
<ToastContainer/>
            <div className="card shadow">
                <div className="card-header text-center">Verification </div>
                <div className="card-body" ng-init="user={}">
                    <form>
                        <div className="mb-3">
                            <label htmlFor="exampleInputEmail1" className="form-label">
                                Two Factor Authentication<span className="text-danger"> *</span>
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                required
                                value={verification}
                                onChange={(e) => setVerification(e.target.value)}
                            />
                        </div>

                        <div className="clearfix">
                            <button
                                onClick={verifiedCode}
                                className="btn btn-success btn-success-dark float-right"
                            >
                                <LoginIcon /> Continue
                            </button>
                        </div>

                    </form>
                </div>
            </div>
            <ToastContainer />

        </Fragment>
    )
}

const mapStateToProps = (state) => ({
    auth: state.auth
})

export default connect(mapStateToProps)(AuthVerificationForm);
