import React, { Fragment, useEffect, useState } from "react";
import { Container } from "@mui/material";
import './TWoFactorEnable.css';
import { connect } from "react-redux";
import {qrCodegenrater}  from '../../redux/actions/authAction';


const TwoFactorEnable=(props)=>{

console.log("qrcode props jkldjsfkj===================> qrCodegenrater",props)

    const [qrImage ,setQrImage] = useState([]);
    const [loginUser,setLoginUser] = useState([]);
    const  [loginId, setLoginId] = useState();

useEffect(()=>{
if(loginId){
    props.qrCodegenrater(loginId)
}

    if(props.auth.user){
        setLoginUser(props.auth.user)
        setLoginId(props.auth.user.id)
    }

if(props.auth){
    setQrImage(props.auth.qrcodeImage.image)
}


qrCodegenrater()

},[])


 const qrCodegenrater = (data) =>{

    fetch(`${urls.base + urls.QrCode}?id=${data}`)
    .then((result)=> result.json()).then((qrdata)=>{
      console.log("qrdata",qrdata)
      if(qrdata){

        dispatch({
          type:QRCODEIMAGE,
          payload:qrdata
        })
      }
    }).catch((error)=>{
      console.log(error)
    })

  };


let image = qrImage 





    return(
        <Fragment>
          <section className="factor_authencate">
         <Container>
          <div className="row">
            <div className="qr_code">

                <img src={`data:image/jpeg;base64,${image}`} />
            </div>
          </div>
         </Container>
          </section>
        </Fragment>
    )
}

const mapStateToProps = (state) => ({
    auth: state.auth,
  });

export default connect(mapStateToProps,{ qrCodegenrater }) (TwoFactorEnable)
