import React, { Fragment, useState } from "react";
import { Login as LoginIcon } from "@mui/icons-material";
import { useLocation } from "react-router-dom";
import './AuthVerification.css';
import {ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { connect } from "react-redux";
import { veriFication } from "../../redux/actions/authAction";

const AuthVerificationForm = (props) => {

console.log("props===========>",props.auth.twoFactorAuthenticate)
    const [verification, setVerification] = useState("");
    const [verificationError, setVerificationError] = useState("Verificaton message is not matched")
    const location = useLocation();


    const verifiedCode = () => {
        props.veriFication(verification);
        // toast.success(verificationError, {
        //     position: "top-right",
        //     className: "app_toast",
        //     autoClose: 1000,
        //     onClose: () => {
        //         window.location.href='/';
        //     },
        //   })

    }


    return (
        <Fragment>

            <div className="card shadow">
                <div className="card-header text-center">Verification </div>
                <div className="card-body" ng-init="user={}">
                    <form>
                        <div className="mb-3">
                            <label htmlFor="exampleInputEmail1" className="form-label">
                                Two Factor Authentication<span className="text-danger"> *</span>
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                required
                                value={verification}
                                onChange={(e) => setVerification(e.target.value)}
                            />
                        </div>

                        <div className="clearfix">
                            <button
                                onClick={verifiedCode}
                                className="btn btn-success btn-success-dark float-right"
                            >
                                <LoginIcon /> Continue
                            </button>
                        </div>

                    </form>
                    {/* <p className="error" >{verificationError}</p> */}
                </div>
            </div>
            <ToastContainer />

        </Fragment>
    )
}

const mapStateToProps=(state)=>(
    {
        auth: state.auth
    }
)

export default connect(mapStateToProps,{veriFication})(AuthVerificationForm);
